# Download Covid and Related Data from the City and County ArcGIS APIs

library(httr)
library(jsonlite)
library(magrittr)

# Function for Fetching Data from ArcGIS Server APIs
fetch_data <- function(url){
  data <- httr::GET(url) %>% httr::content() %>% jsonlite::fromJSON()
  attributes <- data[["features"]][["attributes"]]
  return(attributes)
}
